import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import Input from "../../../atoms/Input/Input";
import "./Account.css";
import Account from "./AccuntCreationComponent";
import * as firebase from "firebase/app";
import { profile } from "../../../../rest/profile/api";
import { useAccount } from "../../../../contexts/AccountContext";

const AccountCreation: React.FC = () => {
    const account = useAccount();
    const history = useHistory();
    const [Namevalid, setNamevalid] = useState(false);
    const [Emailvalid, setEmailvalid] = useState(false);
    const [Passwordvalid, setPasswordvalid] = useState(false);
    const [Loading, setLoading] = useState(false);
    const [err, Seterr] = useState("");
    const [authForm, setAuthForm] = useState({
        ComapanyName: {
            elementType: "comapanyname",
            elementConfig: {
                type: "input",
                placeholder: "株式会社AilaB",
                label: "会社名",
            },
            value: "",
        },
        Email: {
            elementType: "email",
            elementConfig: {
                type: "email",
                placeholder: "tanakataro@rebase.co.jp",
                label: "メールアドレス",
            },
            value: "",
        },
        Password: {
            elementType: "password",
            elementConfig: {
                type: "password",
                placeholder: "qwerty",
                label: "パスワード",
            },
            value: "",
        },
        checkbox: {
            elementType: "checkbox",
            elementConfig: {
                type: "checkbox",
            },
            value: "",
        },
    });

    const inputChangedHandler = (event: KeyboardEvent, controlName: string) => {
        const updatedControls = {
            ...authForm,
            [controlName]: {
                ...authForm[controlName],
                value: (event.target as HTMLInputElement).value,
                valid: authForm[controlName].validation,
                id: controlName,
            },
        };
        setAuthForm(updatedControls);
    };

    const formElementsArray = [];
    for (let key in authForm) {
        formElementsArray.push({
            id: key,
            config: authForm[key],
        });
    }

    let form = formElementsArray.map((formElement) => (
        <React.Fragment key={formElement.id}>
            <Input
                rowkey={formElement.id}
                namevalid={Namevalid}
                emailvalid={Emailvalid}
                passwordvalid={Passwordvalid}
                elementType={formElement.config.elementType}
                elementConfig={formElement.config.elementConfig}
                value={formElement.config.value}
                changed={(event: KeyboardEvent) =>
                    inputChangedHandler(event, formElement.id)
                }
            />
        </React.Fragment>
    ));



    const submit = (e: React.FormEvent<HTMLFormElement>) => {

        e.preventDefault();

        const email = authForm.Email.value;
        const password = authForm.Password.value;
        const name = authForm.ComapanyName.value;
        firebase
            .auth()
            .createUserWithEmailAndPassword(email, password)
            .then((res: any) => {
                history.push("/")
            })
            .catch((err: any) => {
                Seterr("新規登録に失敗しました");
            });
    };
    return <Account form={form} submit={submit} Loading={Loading} err={err} />;
};

export default AccountCreation;
